mapper.select:

select *
from (SELECT rank() over(order by idx desc) AS no,
b.* FROM (select * from study where title like CONCAT('%',#{search},'%')) AS b ) AS list
where no between #{start} AND #{end}

select *, user.name
from (SELECT rank() over(order by idx desc) AS no,
s.* FROM (select * from study) AS s ) AS list
left outer join user
on user.idx = list.create_user_idx
where no between #{start} AND #{end}

mapper.update:
update study
set apply_count=apply_count+1
where idx=#{study_idx}

mapper.select:
select * from (SELECT rank() over(order by idx desc) AS no, b.*
FROM (select * from study



			purpose=#{item}



		and is_online=#{search_option} AND title like CONCAT('%',#{search},'%')


		and is_online=#{search_option}


		and title like CONCAT('%',#{search},'%')


) AS b ) AS list
where no between #{start} AND #{end}

select count(*) from study



			purpose=#{item}



		and is_online=#{search_option} AND title like CONCAT('%',#{search},'%')


		and is_online=#{search_option}


		and title like CONCAT('%',#{search},'%')

mapper.update:
UPDATE study_member 
SET mem_status = '승인'
WHERE idx = #{idx}

UPDATE study_member 
SET mem_status = '승인거부'
WHERE idx = #{idx}

UPDATE study_member 
SET mem_status = '강제탈퇴'
WHERE idx = #{idx}

update study
set approve_count = approve_count - 1
where idx=#{study_idx}

UPDATE study_member
SET mem_status = 'admin', is_admin = 1
WHERE idx = #{idx}

UPDATE study
SET notice=#{notice}
WHERE idx=#{study_idx}

UPDATE study
SET study_status= '진행중'
WHERE idx=#{study_idx}

UPDATE study
SET study_status= '종료'
WHERE idx=#{study_idx}

UPDATE study
SET study_status = '개설취소'
WHERE idx = #{idx}

UPDATE study
SET deadline = deadline + INTERVAL 7 DAY
WHERE idx = #{idx}

UPDATE study
SET end_date = end_date + INTERVAL 7 DAY
WHERE idx = #{idx}

mapper.select:
SELECT *
FROM study_member
WHERE study_idx = #{study_idx} and user_idx = #{user_idx}

mapper.update:
